window.addEvent('domready',function(){$$('.smoothgallery_external_wrapper').each(function(item,index){var galleryItem=$(item).getElement('.smoothgallery_external'),myGallery=new gallery(galleryItem,{timed:true,showCarousel:false,embedLinks:true,slimboxMode:true}),myGalleryThumbnails=$(item).getElement('.smoothgallery_thumbs');if($defined(myGalleryThumbnails)){var items=$(myGalleryThumbnails).getElements('img[class=thumbnail]');items.each(function(item,index){$(item).addEvent('click',function(){myGallery.goTo(index)})})}})});